function pageprocessorBlueLightEmergency(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if($.jStorage.get('termsCondition') == null || $.jStorage.get('termsCondition') == undefined){
            $location.path("/app/TermsCondition/TermsCondition");
    		return;
        }
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var tenantid = $rootScope.metadata.tenantid;
  var mobappid = $rootScope.metadata.mobappid;
  var featureid = "12689";
  $rootScope.back = function() {
    $location.path("/home");
  };

  //$rootScope.defaultGuardians = ['911', '3028571110', '3024543938', '3025735418'];
  var nbOptions = 8;
  var angleStart = -360;

  // jquery rotate animation
  function rotate(li, d) {
    $({
      d: angleStart
    }).animate({
      d: d
    }, {
      step: function(now) {
        $(li)
          .css({
            transform: 'rotate(' + now + 'deg)'
          })
          .find('label')
          .css({
            transform: 'rotate(' + (-now) + 'deg)'
          });
      },
      duration: 0
    });
  }

  // show / hide the options
  function toggleOptions(s) {
    $(s).toggleClass('open');
    var li = $(s).find('li');
    var deg = $(s).hasClass('half') ? 180 / (li.length - 1) : 360 / li.length;
    for (var i = 0; i < li.length; i++) {
      var d = $(s).hasClass('half') ? (i * deg) - 90 : i * deg;
      $(s).hasClass('open') ? rotate(li[i], d) : rotate(li[i], angleStart);
    }
  }

