        } else {            
            $("#NoWeekClasses").hide();
            $("#WeekClasses").show();
        }
        $scope.weekataglance = $scope.weekdata[day].data;
        //console.log($scope.weekataglance);
    }

    $scope.toggleSchedule = function(wag) {
        wag.toggle = !wag.toggle;
        if (wag.toggle) {
            wag.toggleclass = "fa fa-plus fa-2x";
        } else {
            wag.toggleclass = "fa fa-minus fa-2x";
        }
        console.log('#'+wag);
    }

    var curDayIndex = todayDate.getDay() == 0 ? 6 : todayDate.getDay() - 1;
    $scope.weekdata[curDayIndex].style = "btn-selected";
    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function(data) {
        $scope.dropdown = data;
        //$scope.selectedvalue = data.termsStudentHasClasses[0].TERM_CODE;
        //$scope.dropdownselect();
    });

    $scope.dropdownselect = function(selectedvalue) {
        $('#termsel').blur();
        $scope.classeswithouttime = [];
        $scope.showDetail = true;
        for (var i = 0; i < 7; i++) {
            $scope.weekdata[i].data = [];
        }
        $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.listendpoint + $scope.selectedvalue, function(data) {
            
            var WeekAtGlanceTdyDate = new Date();
            var WeekAtGlanceStartDate = new Date();
            var WeekAtGlanceEndDate = new Date();
            WeekAtGlanceStartDate.setDate(WeekAtGlanceTdyDate.getDate() - WeekAtGlanceTdyDate.getDay());
            WeekAtGlanceEndDate.setDate(WeekAtGlanceTdyDate.getDate() + (6 - WeekAtGlanceTdyDate.getDay()));

            angular.forEach(data.weekataglance, function(value, key) {
                var ClassStartDate = new Date(value.START_DATE);
                var ClassEndDate = new Date(value.END_DATE);
                var daysExist = false;
                value.visible = false;
                value.visible = true;
                value.toggleclass = "fa fa-plus fa-2x";
                value.toggle = true;                
                if ((ClassStartDate <= WeekAtGlanceStartDate && ClassEndDate >= WeekAtGlanceEndDate) ||
                    (ClassStartDate <= WeekAtGlanceEndDate && ClassStartDate >= WeekAtGlanceStartDate) ||
                    (ClassEndDate <= WeekAtGlanceEndDate && ClassEndDate >= WeekAtGlanceStartDate)) {
                    value.visible = true;
                    value.toggleclass = "fa fa-plus fa-2x";
                    value.toggle = true;
                    if (value.MON_DAY != "" && value.MON_DAY != null) {
                        this[0].data.push(value);
                        daysExist = true;
                    }
                    if (value.TUE_DAY != "" && value.TUE_DAY != null) {
                        this[1].data.push(value);
                        daysExist = true;
                    }
                    if (value.WED_DAY != "" && value.WED_DAY != null) {
                        this[2].data.push(value);
                        daysExist = true;
                    }
                    if (value.THU_DAY != "" && value.THU_DAY != null) {
                        this[3].data.push(value);
                        daysExist = true;
                    }
                    if (value.FRI_DAY != "" && value.FRI_DAY != null) {
                        this[4].data.push(value);
                        daysExist = true;
                    }
                    if (value.SAT_DAY != "" && value.SAT_DAY != null) {
                        this[5].data.push(value);
                        daysExist = true;
                    }
                    if (value.SUN_DAY != "" && value.SUN_DAY != null) {
                        this[6].data.push(value);
                        daysExist = true;
                    }
                }
                if (value.PLACE == '' || value.PLACE == null || value.PLACE == ' ') {
                    value.PLACE = 'TBA';
                }
                if (value.CLASS_TIME == '' || value.CLASS_TIME == null) {
                    value.CLASS_TIME = 'TBA';
                } else if (value.CLASS_TIME.trim() == '-') {
                    value.CLASS_TIME = 'TBA';
                }
                value.showweekclasses = daysExist;
                if (!daysExist) {
                    $scope.classeswithouttime.push(value);
                }
            }, $scope.weekdata);
            $scope.listdata = data.weekataglance;
            console.log('listdata');
            console.log($scope.listdata);
            $scope.selectDay(curDayIndex);
        });
    }
    
    $rootScope.back = function() {
        if ($routeParams.from) {
            //window.location.href = "index.html#app/" + $routeParams.from + "/" + $routeParams.from;
            //$location.path("/app/" + $routeParams.from + "/" + $routeParams.from);

            if (device.platform == 'iOS') {
                history.go(-1);
            } else {
                navigator.app.backHistory();
            }
        } else {
            window.location.href = "index.html#home";
        }
    }    
}

