        sunday = ($scope.weekdata[6].selected == 'Y') ? 'U':'';        

        var searchURL = "/services/student/bannerregCartclasslookup/termCode=" + $scope.selectedvalue +
            "&subjectCode=" + $scope.selectedSubject +
