function pageprocessorAccTranscript(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerblockTranscriptsGrades", function(data) {
        if (data.blockTranscriptsGrades.length > 0 ) {
             if (data.blockTranscriptsGrades[0].BLOCKTRANSCRIPTS == 'Y' || data.blockTranscriptsGrades[0].BLOCKGRADES == 'Y' ) {
                if (window.device) {
                    var doNothing = function() {
                        $rootScope.$apply(function() {
                            $location.path("/home");
                        });
                    };
                    navigator.notification.alert("Your transcript is not available due to holds on your record.", doNothing, 'Alert', 'Ok');
                } else {
                    apprise("Your transcript is not available due to holds on your record.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        $rootScope.$apply(function() {
                            $location.path("/home");
                        });
                    });
                }
             } else {
                $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function(data1) {
                    $scope.dropdown = data1;
                    if ($scope.dropdown.transcripttypedropdown.length > 0) {
                        $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[0].TRANSCRIPT_CODE;
                        $('#credittype').blur();
                    }                     
/*                    if ($scope.dropdown.transcriptleveldropdown.length == 1) {
                        $scope.selectedvalue = $scope.dropdown.transcriptleveldropdown[0].LEVEL_CODE;
                        $scope.dropdownselect($scope.selectedvalue);
                    }*/
                });            
             }
        } else {
            $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function(data1) {
                $scope.dropdown = data1;
                if ($scope.dropdown.transcripttypedropdown.length > 0) {
                    $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[0].TRANSCRIPT_CODE;
                    $('#credittype').blur();

                }                 
/*                if ($scope.dropdown.transcriptleveldropdown.length == 1) {
                    $scope.selectedvalue = $scope.dropdown.transcriptleveldropdown[0].LEVEL_CODE;
                    $scope.dropdownselect($scope.selectedvalue);
                } */               
            });             
        }


    });

    $scope.dropdownselect = function(selectedvalue) {
        $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.listendpoint + $scope.selectedvalue, function(data) {
            $scope.listdata = data;
            $scope.creditselect($scope.creditselectedvalue);
            $('#level').blur();
        });
    }
    $scope.creditselect = function(selectedvalue) {
        $scope.transfer = null;
        $scope.institution = null;
        $scope.transcripts = null;
        // if ($scope.creditselectedvalue == "transfer") {
            var transfer = {
                attempted: 0,
                passed: 0,
                earned: 0,
                gpa: 0,
                credit: 0
            };
            transfer.attempted = angular.isUndefined($scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS;
            transfer.passed = angular.isUndefined($scope.listdata.transfer_data_total[0][0].PASSED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].PASSED_HOURS;
            transfer.earned = angular.isUndefined($scope.listdata.transfer_data_total[0][0].EARNED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].EARNED_HOURS;
            transfer.gpa = angular.isUndefined($scope.listdata.transfer_data_total[0][0].GPA_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].GPA_HOURS;
            transfer.credit = angular.isUndefined($scope.listdata.transfer_data_total[0][0].QUALITY_POINTS) ? 0 : $scope.listdata.transfer_data_total[0][0].QUALITY_POINTS;
            $scope.transfer = transfer;
        // } else if ($scope.creditselectedvalue == "institution") {
            $scope.institution = "data";
            var totalinst = {
                ah: 0,
                ph: 0,
                eh: 0,
                gpah: 0,
                qp: 0
            };
            angular.forEach($scope.listdata.institutioncredits, function(value, key) {
                this.ah += parseFloat(value.ATTEMPTED_HOURS);
                this.ph += parseFloat(value.SHRTCKG_PASS_HOURS);
                this.eh += parseFloat(value.SHRTCKG_EARN_HOURS);
                this.gpah += parseFloat(value.SHRTCKG_GPA_HOURS);
                this.qp += parseFloat(value.SHRTCKG_QUAL_PTS);
            }, totalinst);
            $scope.insttotal = totalinst;

        // } else if ($scope.creditselectedvalue == "transcripts") {
            $scope.transcripts = "data";
            angular.forEach($scope.listdata.transcripttotal, function(value, key) {
                if (value.TRANSCRIPT_TYPE == "I") {
                    $scope.totalinst = value;
                } else if (value.TRANSCRIPT_TYPE == "T") {
                    $scope.totaltrans = value;
                } else if (value.TRANSCRIPT_TYPE == "Z") {
                    $scope.cumulative = value;
                }
            });
        // }

    }
}

function pageprocessorFinalGradeB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
