function pageprocessorSocialMedia3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

 var appnames = pagedef.appnames.split(",");
    var results = [];
    for (var i = 0; i < appnames.length; i++) {
        for (var j = 0; j < $rootScope.apps.length; j++) {
            if ($rootScope.apps[j].displayname == appnames[i]) {
                results.push($rootScope.apps[j]);
            }
        }
    }
    $scope.groupapps = results;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent")
        .append(data1);
    var homedata = $("#homedata");
    var loadpage = function() {
        $("#appContent")
            .html("");
        var data1 = $compile($(pagedef.datatemplate))($scope);
        $("#appContent")
            .append(data1);
        var homedata = $("#homedata");
        var iconwidth = 64;
        var sampleicon = $('#sampleicon');
        var calculated = sampleicon.outerWidth(true);
        if (calculated != 0) {
            iconwidth = sampleicon.outerWidth(true) + 24;
        }
        var width = $(window)
            .width() > 780 ? $(window)
            .width() - 280 : $(window)
            .width();
        var cols = Math.floor(width / iconwidth);
        var rows = Math.floor(($(window)
            .height() - 200) / iconwidth);
        var icon;
        for (_i = 0, _len = $scope.groupapps.length; _i < _len; _i++) {
            icon = $scope.groupapps[_i];
            var markup = '<li><a href="#app/' + icon.name + "/" + icon.name + '?from=SocialMedia3"><img src="' + icon.logo + '" class="icon"></img></a>' +
                '<div class="campuseai-Info text-center" style="width:' + calculated + 'px;overflow:hidden;text-overflow: ellipsis;">' + icon.displayname + '</div></li>';
            homedata.append(markup);
        }

        //AK added for promptumenu
        $("#homedata")
            .promptumenu({
                width: (width - 24),
                height: ($(window)
                    .height() - 200),
                rows: rows,
                columns: cols,
                direction: 'horizontal',
                pages: true
            });
    }

    loadpage();
    var handleEmergencyOrientation = function() {
        if (orientation == 0) {
            setTimeout(function() {
                loadpage()
            }, 350);
        } else if (orientation == 90) {
            setTimeout(function() {
                loadpage()
            }, 350);
        } else if (orientation == -90) {
            setTimeout(function() {
                loadpage()
            }, 350);
        } else if (orientation == 180) {
            setTimeout(function() {
                loadpage()
            }, 350);
        } else {}
    }
    window.handleOrientation = handleEmergencyOrientation;
    window.addEventListener('orientationchange', handleEmergencyOrientation, false);
    $scope.$on('$destroy', function() {
        window.removeEventListener('orientationchange', handleEmergencyOrientation, false);
        window.handleOrientation = null;
    });

    $rootScope.back = function() {
        if ($routeParams.from) {
            //window.location.href = "index.html#app/" + $routeParams.from + "/" + $routeParams.from;
            //$location.path("/app/" + $routeParams.from + "/" + $routeParams.from);

            if (device.platform == 'iOS') {
                history.go(-1);
            } else {
                navigator.app.backHistory();
            }
        } else {
            window.location.href = "index.html#home";
        }
    }
}

