function pageprocessorVideosVideosDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var video = $rootScope.videos[$routeParams.index];
  var videoID = video.id.videoId

  var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyAC5TPbRtJ_dZTWSVzyIQY_QzLLJANuR-o&part=snippet,contentDetails,statistics,status";
  $http.get(url).success(function(data) {

    console.log(JSON.stringify(data));
    $scope.title = data.items[0].snippet.localized.title;
    $scope.description = data.items[0].snippet.localized.description;
    $scope.trustSrc = function() {
      return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID);
    };


    $scope.linkify = function() {
      var inputText = $scope.description;
      var replacedText, replacePattern1, replacePattern2, replacePattern3;

      //URLs starting with http://, https://, or ftp://
      replacePattern1 = /(\b(https?|ftp):\/\/[-A-Z0-9+&@#\/%?=~_|!:,.;]*[-A-Z0-9+&@#\/%=~_|])/gim;
      replacedText = inputText.replace(replacePattern1, '<a href="$1" target="_blank">$1</a>');

      //URLs starting with "www." (without // before it, or it'd re-link the ones done above).
      replacePattern2 = /(^|[^\/])(www\.[\S]+(\b|$))/gim;
      replacedText = replacedText.replace(replacePattern2, '$1<a href="http://$2" target="_blank">$2</a>');

      //Change email addresses to mailto:: links.
      replacePattern3 = /(([a-zA-Z0-9\-\_\.])+@[a-zA-Z\_]+?(\.[a-zA-Z]{2,6})+)/gim;
      replacedText = replacedText.replace(replacePattern3, '<a href="mailto:$1">$1</a>');
      return $sce.trustAsHtml(replacedText);
    }

    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

  }).error(function(data) {
    $.unblockUI();
  });

  setTimeout(function() {
    $("#content a").on("click", function(eve) {

      var link = $(this).attr('href');
      if (link.indexOf("mailto:") > -1 || link.indexOf("tel:") > -1) {} else {
        eve.preventDefault();

        window.open(link, "_blank", "location=yes,enableViewportScale=yes");
      }
    });
  }, 1000);

  try {
    var onPause = function() {
      $('iframe').attr('src', $('iframe').attr('src'));
    }

    document.addEventListener("pause", onPause, false);
  } catch (e) {
    // alert(e)
  }


}

