        postdata += "countycode=" + (address.COUNTY_CODE == null ? "" : address.COUNTY_CODE) + "&";
        postdata += "nationcode=" + (address.NATION_CODE == null ? "" : address.NATION_CODE) + "&";
        postdata += "zip=" + (address.ZIP == null ? "" : address.ZIP) + "&";
        if (address.primaryphone) {
            postdata += "phoneareacode=" + (address.primaryphone.AREA_CODE == null ? "" : address.primaryphone.AREA_CODE) + "&";
            postdata += "phonenumber=" + (address.primaryphone.PHONE_NUMBER == null ? "" : address.primaryphone.PHONE_NUMBER) + "&";
            postdata += "phoneextn=" + (address.primaryphone.EXTENSION == null ? "" : address.primaryphone.EXTENSION) + "&";
            postdata += "phonecountrycode=" + (address.primaryphone.COUNTRY_CODE == null ? "" : address.primaryphone.COUNTRY_CODE) + "&";
            postdata += "intlaccess=" + (address.primaryphone.INTERNATIONAL_ACCESS == null ? "" : address.primaryphone.INTERNATIONAL_ACCESS) + "&";
            postdata += "unlistedind=" + (address.primaryphone.UNLISTED_IND == null ? "" : address.primaryphone.UNLISTED_IND) + "&";            
        } else {
            postdata += "phoneareacode=" + "" + "&";
            postdata += "phonenumber=" + "" + "&";
            postdata += "phoneextn=" + "" + "&";
            postdata += "phonecountrycode=" + "" + "&";
            postdata += "intlaccess=" + "" + "&";
            postdata += "unlistedind=" + "" + "&";            
        }

        var addlseqnos = "";
        var addltelecodes = "";
        var addlcountrycodes = "";
        var addlareacodes = "";
        var addlphonenumbers = "";
        var addlphoneextns = "";
        var addlintlaccess = "";
        var addlunlistedinds = "";
        var addldelinds = "";
        angular.forEach(address.additionalphones, function(value, key) {
            if (key == 0) {
                addlseqnos += value.PHONE_SEQ;
                addltelecodes += value.PHONE_CODE;
                addlcountrycodes += value.COUNTRY_CODE;
                addlareacodes += value.AREA_CODE;
                addlphonenumbers += value.PHONE_NUMBER;
                addlphoneextns += value.EXTENSION;
                addlintlaccess += value.INTERNATIONAL_ACCESS;
                addlunlistedinds += value.UNLISTED_IND;
                if (value == contact) {
                    addldelinds += "Y";
                }
            } else {
                addlseqnos += "," + value.PHONE_SEQ;
