                addlcountrycodes += "," + value.COUNTRY_CODE;
                addlareacodes += "," + value.AREA_CODE;
                addlphonenumbers += "," + value.PHONE_NUMBER;
                addlphoneextns += "," + value.EXTENSION;
                addlintlaccess += "," + value.INTERNATIONAL_ACCESS;
                addlunlistedinds += "," + value.UNLISTED_IND;
                if (value == contact) {
                    addldelinds += ",Y";
                } else {
                    addldelinds += ",";
                }
            }
        });
        postdata += "addlseqnos=" + addlseqnos + "&";
        postdata += "addltelecodes=" + addltelecodes + "&";
        postdata += "addlcountrycodes=" + addlcountrycodes + "&";
        postdata += "addlareacodes=" + addlareacodes + "&";
        postdata += "addlphonenumbers=" + addlphonenumbers + "&";
