function pageprocessorDirectory2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $scope.directory = null;
  $rootScope.directory = null;
  $rootScope.DirectoryResult = null;
  $('#appContent')
    .append(data1);
  var dv = '<div class="list-group-item" style="background: none repeat scroll 0 0 #313131;box-shadow: 0 0 50px #fff inset;padding:0px;">' +
    '<form id="srch" class="form-horizontal">' +
    '<div class="list-group-item" style="padding: 8px 10px">' +
    '<input type="search" id="fname" ng-model="fname" class="form-control" placeholder="First Name">' +
    '</div>' +
    '<div class="list-group-item" style="padding: 8px 10px">' +
    '<input type="search" id="lname" ng-model="lname" class="form-control" placeholder="Last Name">' +
    '</div>' +
    '<div class="list-group-item" style="padding: 8px 10px">' +
    '<button class="btn btn-primary btn-block btn-sm" ng-click="search()">Search</button>' +
    '</div>' +
    '</form>' +
    '</div>';
  $(".app-content")
    .prepend($compile(dv)($scope));

  /*$('#srch')
      .on('submit', function(event) {
          event.preventDefault();
          $scope.search();
          $scope.$digest();
          $("#srchbtn")
              .focus();
          return false;
      });*/
  $scope.search = function() {
    //$scope.directory = null;
    //var searchTxt = $("#Directorytxt").val();
    var searchparams = "";
    if (($scope.fname == "" || $scope.fname == null) && ($scope.lname == "" || $scope.lname == null)) {
      //searchTxt = "";
      if (window.device) {
        navigator.notification.alert("Please enter first name or lastname", null, 'Directory Search', 'OK');
      } else {
        apprise("Please enter first name or last name", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {});
      }
      return;

    } else {
      searchparams = "?fname=" + ($scope.fname ? $scope.fname : '') + "&lname=" + ($scope.lname ? $scope.lname : '');
    }
    $rootScope.Directoryval = $scope.Directoryval;
    $.blockUI();
    //var url = "https://kryptosmw.kryptosmobile.com/webadapter1/services/public/LIPSCOMB/LW/lipscombdirectorysearch" + searchTxt;
    var url = pagedef.endpoint + searchparams;
    var proxyMethod = "GET";
    var proxyData = "";
 console.log("url get api "+ url);
    if (window.device) {} else {
      url = "/websimulator/json?url=" + url;
      proxyMethod = "POST";
      proxyData = {
        method: "GET"
          //body: searchparams
      }
    }
    $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      })
      .
    success(function(data, status, headers, config) {
        $.unblockUI();
        console.log("sucess========"+data);
        $scope.directory = data.directory;
        $rootScope.directory = data.directory;
        $rootScope.DirectoryResult = data.directory;
      })
      .
    error(function(data, status, headers, config) {
      console.log("data error"+ JSON.stringify(data));
      $.unblockUI();

    });
  }
  $scope.filterresult = function(alpha) {
    $rootScope.Directoryval = null;
    $.blockUI();
    var url = pagedef.endpoint + "?query=" + alpha.label;
    var proxyMethod = "GET";
    var proxyData = {};
    if (window.device) {} else {
      url = "/websimulator/json?url=" + url;
      proxyMethod = "POST";
      proxyData = {
        method: "GET"
      }
    }
    $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      })
      .
    success(function(data, status, headers, config) {
        $.unblockUI();
        console.log(data);
        $scope.directory = data.directory;
        $rootScope.directory = data.directory;
        $rootScope.DirectoryResult = data.directory;
      })
      .
    error(function(data, status, headers, config) {
      $.unblockUI();

    });
  }

}
